import React from 'react';
import './Recommendations.css';

const Recommendations = () => {
  const places = [
    {
      name: 'Goa Beach',
      description: 'Enjoy the sunny beaches and vibrant culture.',
      imageUrl: '/images/goa-beach.jpg', // Make sure the image path is correct
    },
    {
      name: 'Mysore Palace',
      description: 'Explore the historical and majestic Mysore Palace.',
      imageUrl: '/images/mysore-palace.jpg',
    },
    {
      name: 'Taj Mahal',
      description: 'Visit the symbol of love in Agra.',
      imageUrl: '/images/taj-mahal.jpg',
    },
  ];

  return (
    <div className="recommendations-container">
      <h2>Top Recommendations</h2>
      <div className="card-container">
        {places.map((place, index) => (
          <div className="card" key={index}>
            <img src={place.imageUrl} alt={place.name} />
            <div className="card-content">
              <h3>{place.name}</h3>
              <p>{place.description}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Recommendations;
