import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';

const Navbar = () => {
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <>
      {/* Header Section */}
      <header className="header">
        <div className="header-overlay"></div>
        <div className="header-content">
          <h1 className="header-title">Explore India with AI</h1>
          <p className="header-description">
            Get personalized travel recommendations and explore the best destinations in India.
          </p>
        </div>
      </header>

      {/* Navbar Section */}
      <nav className="navbar">
        {/* Logo */}
        <Link to="/" className="navbar-logo">
          TravelPlanner
        </Link>

        {/* Hamburger Menu Button (visible on mobile) */}
        <button className="navbar-toggle" onClick={toggleMenu}>
          â˜°
        </button>

        {/* Navbar Links */}
        <div className={`navbar-links ${menuOpen ? 'open' : ''}`}>
          <Link to="/" onClick={() => setMenuOpen(false)}>Home</Link>
          <Link to="/about" onClick={() => setMenuOpen(false)}>About</Link>
          <Link to="/contact" onClick={() => setMenuOpen(false)}>Contact</Link>
          <Link to="/recommendations" onClick={() => setMenuOpen(false)}>Recommendations</Link>
        </div>
      </nav>
    </>
  );
};

export default Navbar;
