import React, { useState } from 'react';
import './Recommendations.css';

const Recommendations = () => {
  const [city, setCity] = useState('');
  const [recommendations, setRecommendations] = useState([]);
  const [error, setError] = useState('');

  const cities = [
    "Delhi", "Mumbai", "Bengaluru", "Jaipur", "Goa", "Chennai",
    "Kolkata", "Hyderabad", "Ahmedabad", "Pune"
  ];

  const handleFetchRecommendations = async (selectedCity) => {
    setCity(selectedCity);  // Set the selected city
    setRecommendations([]);  // Clear previous results
    setError('');

    try {
      // Simulating API call with dummy data
      const dummyData = {
        "Delhi": [
          { name: "India Gate", description: "Historic monument and war memorial." },
          { name: "Red Fort", description: "A UNESCO World Heritage Site." },
          { name: "Qutub Minar", description: "A towering example of Indo-Islamic architecture." }
        ],
        "Mumbai": [
          { name: "Gateway of India", description: "Iconic landmark overlooking the Arabian Sea." },
          { name: "Marine Drive", description: "Scenic boulevard along the coastline." },
          { name: "Elephanta Caves", description: "Ancient rock-cut caves on an island." }
        ]
      };

      const cityRecommendations = dummyData[selectedCity];
      if (cityRecommendations) {
        setRecommendations(cityRecommendations);
      } else {
        throw new Error('No recommendations found for the selected city.');
      }
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div className="recommendations-container">
      <h1>Explore the Best Places to Visit</h1>
      <p>Select a city below to see the recommendations.</p>
      <div className="city-list">
        {cities.map((cityName, index) => (
          <button
            key={index}
            className="city-button"
            onClick={() => handleFetchRecommendations(cityName)}
          >
            {cityName}
          </button>
        ))}
      </div>
      
      {error && <p className="error-message">{error}</p>}

      {city && recommendations.length === 0 && <p>Loading recommendations for {city}...</p>}

      <div className="results">
        {recommendations.map((place, index) => (
          <div key={index} className="recommendation-card">
            <h3>{place.name}</h3>
            <p>{place.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Recommendations;
