import React, { useState } from "react";
import axios from "axios";

const Recommender = () => {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState("");

    const sendMessage = async () => {
        if (!input.trim()) return;
        
        const newMessages = [...messages, { sender: "user", text: input }];
        setMessages(newMessages);

        try {
            const response = await axios.post("http://localhost:5000/chat", { message: input });
            setMessages([...newMessages, { sender: "bot", text: response.data.response }]);
        } catch (error) {
            console.error("Chatbot error:", error);
        }

        setInput("");
    };

    return (
        <div style={{ maxWidth: "600px", margin: "auto", padding: "20px", border: "1px solid #ccc", borderRadius: "8px" }}>
            <h2>Recommender</h2>
            <div style={{ height: "400px", overflowY: "auto", marginBottom: "10px", border: "1px solid #ddd", padding: "10px" }}>
                {messages.map((msg, index) => (
                    <div key={index} style={{ textAlign: msg.sender === "user" ? "right" : "left", margin: "5px 0" }}>
                        <strong>{msg.sender === "user" ? "You: " : "Recommender: "}</strong>
                        {msg.text}
                    </div>
                ))}
            </div>
            <input 
                type="text" 
                value={input} 
                onChange={(e) => setInput(e.target.value)} 
                placeholder="Ask for recommendations..." 
                style={{ width: "75%", padding: "5px" }} 
            />
            <button onClick={sendMessage} style={{ width: "20%", marginLeft: "5%" }}>Send</button>
        </div>
    );
};

export default Recommender;
