// src/components/Recommendations.js
import React from 'react';
import './Recommendations.css';

const Recommendations = () => {
  const places = [
    { id: 1, name: 'Goa', description: 'Beaches and nightlife' },
    { id: 2, name: 'Manali', description: 'Mountain adventure' },
    { id: 3, name: 'Kerala', description: 'Backwaters and beaches' },
  ];

  return (
    <div className="recommendations-container">
      <h1>Our Top Recommendations</h1>
      <div className="card-container">
        {places.map((place) => (
          <div key={place.id} className="card">
            <img src={`https://your-image-url-${place.id}.jpg`} alt={place.name} />
            <div className="card-content">
              <h3>{place.name}</h3>
              <p>{place.description}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Recommendations;
