import React from 'react';
import './Home.css';

const Home = () => {
  const featureData = [
    {
      title: 'Explore Hotels',
      description: 'Find the best hotels for a comfortable stay.',
      img: 'https://source.unsplash.com/100x100/?hotel',
    },
    {
      title: 'Adventure Activities',
      description: 'Experience thrilling adventures and activities.',
      img: 'https://source.unsplash.com/100x100/?adventure',
    },
    {
      title: 'Local Food',
      description: 'Discover local cuisines and food experiences.',
      img: 'https://source.unsplash.com/100x100/?food',
    },
    {
      title: 'Cultural Tours',
      description: 'Dive into the rich cultural heritage of India.',
      img: 'https://source.unsplash.com/100x100/?culture',
    },
    {
      title: 'Wellness Retreats',
      description: 'Relax at peaceful wellness retreats.',
      img: 'https://source.unsplash.com/100x100/?wellness',
    },
  ];

  return (
    <div className="home-container">
      <div className="hero-section">
        <h1>Welcome to AI Travel Planner</h1>
        <p>Let us guide you to your dream destinations.</p>
        <button className="cta-button">Plan Your Trip</button>
      </div>

      <div className="features-section">
        {featureData.map((feature, index) => (
          <div className="feature-card" key={index}>
            <img src={feature.img} alt={feature.title} />
            <div className="feature-card-content">
              <h2>{feature.title}</h2>
              <p>{feature.description}</p>
            </div>
          </div>
        ))}
      </div>

      <footer className="footer">
        <div className="footer-content">
          <p>Â© 2025 AI Travel Planner. All rights reserved.</p>
          <div className="footer-links">
            <a href="mailto:info@aiplanner.com">Contact Us</a>
            <a href="/privacy">Privacy Policy</a>
            <a href="/terms">Terms & Conditions</a>
          </div>
        </div>
      </footer>
    </div>
  );
};

export default Home;
